package tupple

import (
	"testing"
	"time"
)

func findTuples(arr1, arr2, arr3, arr4 []int) (int, float64, string) {
	start := time.Now()
	n := len(arr1)

	// Check if all arrays have the same length
	if n != len(arr2) || n != len(arr3) || n != len(arr4) {
		return 0, 0, "array length is not consistent"
	}

	// Find all valid tuples
	count := 0
	for a := 0; a < n; a++ {
		for b := 0; b < n; b++ {
			for c := 0; c < n; c++ {
				for d := 0; d < n; d++ {
					if arr1[a]+arr2[b]+arr3[c]+arr4[d] == 12 {
						count++
					}
				}
			}
		}
	}

	duration := time.Since(start).Seconds()
	return count, duration, ""
}

func findTuplesChan(arr1, arr2, arr3, arr4 []int) (int, float64, string) {
	start := time.Now()
	n := len(arr1)

	// Check if all arrays have the same length
	if n != len(arr2) || n != len(arr3) || n != len(arr4) {
		return 0, 0, "array length is not consistent"
	}

	// Channel to collect the results
	results := make(chan int, n*n*n*n)
	defer close(results)

	// Worker function to process a subset of the tuples
	worker := func(start, end int) {
		localCount := 0
		for a := start; a < end; a++ {
			for b := 0; b < n; b++ {
				for c := 0; c < n; c++ {
					for d := 0; d < n; d++ {
						if arr1[a]+arr2[b]+arr3[c]+arr4[d] == 12 {
							localCount++
						}
					}
				}
			}
		}
		results <- localCount
	}

	// Launch goroutines
	numWorkers := 4 // Number of concurrent workers
	chunkSize := (n + numWorkers - 1) / numWorkers
	for i := 0; i < numWorkers; i++ {
		startIndex := i * chunkSize
		endIndex := startIndex + chunkSize
		if endIndex > n {
			endIndex = n
		}
		go worker(startIndex, endIndex)
	}

	// Collect results
	totalCount := 0
	for i := 0; i < numWorkers; i++ {
		totalCount += <-results
	}

	duration := time.Since(start).Seconds()
	return totalCount, duration, ""
}

